##import libraries
import numpy as np
import pandas as pd
from sklearn.preprocessing import StandardScaler
from sklearn.model_selection import train_test_split
from sklearn import svm
from sklearn.metrics import accuracy_score
import joblib

# Load dataset
diabetes_dataset = pd.read_csv("/content/diabetes.csv")  # Upload manually if needed

# âœ… 3. Split data into features and labels
X = diabetes_dataset.drop(columns="Outcome", axis=1)
Y = diabetes_dataset["Outcome"]

# Standardize the features
scaler = StandardScaler()
scaler.fit(X)
X_scaled = scaler.transform(X)

# Train-test split
X_train, X_test, Y_train, Y_test = train_test_split(
    X_scaled, Y, test_size=0.2, stratify=Y, random_state=2
)

# Train the model
classifier = svm.SVC(kernel="linear")
classifier.fit(X_train, Y_train)

# Evaluate
train_accuracy = accuracy_score(classifier.predict(X_train), Y_train)
test_accuracy = accuracy_score(classifier.predict(X_test), Y_test)

print("Training Accuracy:", train_accuracy)
print("Test Accuracy:", test_accuracy)

# Save model and scaler
joblib.dump(classifier, "diabetes_model.sav")
joblib.dump(scaler, "scaler.sav")

